self.onInit = function() {
    var scope = self.ctx.$scope;
    var id = self.ctx.$scope.$injector.get('utils').guid();
    scope.formId = "form-"+id;
    scope.ctx = self.ctx;
}

self.onResize = function() {
    self.ctx.$scope.$broadcast('gateway-form-resize', self.ctx.$scope.formId);
}

